const twStory = document.querySelector("tw-story");
const twPassage = document.querySelector("tw-passage");
const greyBorder = document.querySelectorAll(".greyborder");
const dividerLine = document.querySelectorAll(".divider-line");
const chapterHeadingLeft = document.querySelectorAll(".chapter-heading-left");
const chapterHeadingRight = document.querySelectorAll(".chapter-heading-right");
const chapterLabel = document.querySelectorAll(".chapter-label");
const twStoryTitle = document.querySelectorAll(".tw-story-title");
const twStoryHeading = document.querySelectorAll(".tw-story-heading");

const passageHeight = twPassage.scrollHeight;
const passageScrollTop = window.scrollY;
const viewportHeight = window.innerHeight;

const maxScrollTop = passageHeight - viewportHeight;
const scrollPercentage = 1 - Math.min(passageScrollTop / maxScrollTop, 1);

// Calculate the background color transition
const bgColor = `rgb($245 - 245 * scrollPercentage, $222 - 222 * scrollPercentage, $179 - 179 * scrollPercentage)`;
twPassage.style.backgroundColor = bgColor;
twStory.style.backgroundColor = bgColor;

// Calculate the text color transition
const textColor = `rgb($74 + (255 - 74) * scrollPercentage, $36 + (255 - 36) * scrollPercentage, $36 + (255 - 36) * scrollPercentage)`;
twStory.style.color = textColor;

// Add highlight for contrast during transition
if (scrollPercentage > 0)
twStory.classList.add("highlight");
else
twStory.classList.remove("highlight");

